import React, { useState } from 'react'
import Card from "../UI/Card";
import classes from "./AddUser.module.css";
import Button from "../UI/Button"

const AddUser = (props) => {

    const [enteredName, setEnteredName] = useState('');
    const [enteredAge, setEnteredAge] = useState('');

    const addUserHandler = (e) => {
        e.preventDefault();
        if(enteredAge.trim().length===0 || enteredName.trim().length===0) return;
        if(+enteredAge <= 0) return;
        props.onAddUser(enteredName, enteredAge);
        setEnteredName('');
        setEnteredAge('');
    }
    const NameChangeHandler=(e)=>{
        setEnteredName(e.target.value);
    }
    const AgeChangeHandler=(e)=>{
        setEnteredAge(e.target.value);
    }

    return (
        <Card className={classes.input}>
            <form onSubmit={addUserHandler}>
                <label htmlFor="username">Username</label>
                <input id="username" type="text" onChange={NameChangeHandler} value={enteredName} />
                <label htmlFor="age">Age (years)</label>
                <input type="number" id="age" onChange={AgeChangeHandler} value={enteredAge} />
                <Button type="submit">Add User</Button>
            </form>
        </Card>

    );
}

export default AddUser;
